import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Fade from 'react-reveal/Fade';
import styled, { keyframes } from 'styled-components';

// Animations
const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px); }
  50% { transform: translateY(-20px); }
`;

const pulse = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
`;

const HomeSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  background: linear-gradient(-45deg, ${({ theme }) => theme.background}, ${({ theme }) => theme.background}dd, ${({ theme }) => theme.accentColor}20);
  background-size: 400% 400%;
  animation: ${gradientAnimation} 15s ease infinite;
  padding-top: 100px;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 20% 80%, ${({ theme }) => theme.accentColor}10 0%, transparent 50%),
                radial-gradient(circle at 80% 20%, ${({ theme }) => theme.accentColor}15 0%, transparent 50%);
    pointer-events: none;
  }
`;

const HeroTitle = styled.h1`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 20px;
  background: linear-gradient(135deg, ${({ theme }) => theme.color}, ${({ theme }) => theme.accentColor});
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.2;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const HeroSubtitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 400;
  margin-bottom: 30px;
  color: ${({ theme }) => theme.accentColor};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: ${({ theme }) => theme.accentColor};
    animation: ${gradientAnimation} 3s ease-in-out infinite;
  }

  @media (max-width: 768px) {
    font-size: 1.4rem;
  }
`;

const TypewriterText = styled.span`
  position: relative;

  &::after {
    content: '|';
    animation: blink 1s infinite;
    margin-left: 2px;
  }

  @keyframes blink {
    0%, 50% { opacity: 1; }
    51%, 100% { opacity: 0; }
  }
`;

const HeroDescription = styled.p`
  font-size: 1.2rem;
  color: ${({ theme }) => theme.color};
  opacity: 0.8;
  margin-bottom: 30px;
  line-height: 1.6;
  max-width: 500px;
`;

const CTAContainer = styled.div`
  display: flex;
  gap: 20px;
  margin: 30px 0;
  flex-wrap: wrap;

  @media (max-width: 768px) {
    justify-content: center;
  }
`;

const CTAButton = styled(Button)`
  padding: 12px 30px;
  font-weight: 600;
  border-radius: 50px;
  border: 2px solid ${({ theme }) => theme.accentColor};
  background: ${props => props.primary ? ({ theme }) => theme.accentColor : 'transparent'};
  color: ${props => props.primary ? 'white' : ({ theme }) => theme.accentColor};
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: -100%;
    width: 100%;
    height: 100%;
    background: linear-gradient(90deg, transparent, rgba(255,255,255,0.2), transparent);
    transition: left 0.5s;
  }

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 8px 25px ${({ theme }) => theme.accentColor}40;

    &::before {
      left: 100%;
    }
  }
`;

const SocialContainer = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 30px;

  @media (max-width: 991px) {
    justify-content: center;
  }
`;

const SocialLink = styled.a`
  display: inline-flex;
  align-items: center;
  justify-content: center;
  width: 50px;
  height: 50px;
  border-radius: 50%;
  background: linear-gradient(135deg, ${({ theme }) => theme.accentColor}, ${({ theme }) => theme.accentColor}80);
  color: white;
  text-decoration: none;
  font-size: 1.5rem;
  transition: all 0.3s ease;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: linear-gradient(135deg, ${({ theme }) => theme.color}20, transparent);
    opacity: 0;
    transition: opacity 0.3s ease;
  }

  &:hover {
    transform: scale(1.1) rotate(5deg);
    box-shadow: 0 8px 25px ${({ theme }) => theme.accentColor}40;

    &::before {
      opacity: 1;
    }
  }
`;

const HeroImageContainer = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: center;

  @media (max-width: 991px) {
    margin-top: 40px;
  }
`;

const HeroImage = styled.img`
  width: 100%;
  max-width: 400px;
  height: auto;
  border-radius: 20px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  object-fit: cover;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 30px 60px rgba(0, 0, 0, 0.15);
  }

  @media (max-width: 768px) {
    max-width: 300px;
  }

  @media (max-width: 576px) {
    max-width: 250px;
  }
`;

const FloatingElement = styled.div`
  position: absolute;
  width: 60px;
  height: 60px;
  background: ${({ theme }) => theme.accentColor};
  border-radius: 50%;
  opacity: 0.1;
  animation: ${float} 6s ease-in-out infinite;

  &:nth-child(1) {
    top: 10%;
    left: 10%;
    animation-delay: 0s;
  }

  &:nth-child(2) {
    top: 70%;
    right: 10%;
    animation-delay: 2s;
  }

  &:nth-child(3) {
    bottom: 20%;
    left: 20%;
    animation-delay: 4s;
  }
`;

const Home = () => {
  const [homeData, setHomeData] = useState(null);
  const [socialData, setSocialData] = useState(null);
  const [currentRole, setCurrentRole] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    fetch('/profile/home.json')
      .then(response => response.json())
      .then(data => setHomeData(data))
      .catch(error => console.error('Error loading home data:', error));

    fetch('/profile/social.json')
      .then(response => response.json())
      .then(data => setSocialData(data))
      .catch(error => console.error('Error loading social data:', error));
  }, []);

  useEffect(() => {
    if (homeData && homeData.roles) {
      const interval = setInterval(() => {
        setCurrentRole((prev) => (prev + 1) % homeData.roles.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [homeData]);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  if (!homeData || !socialData) return null;

  return (
    <HomeSection id="home">
      <Container>
        <Row className="align-items-center">
          <Col lg={6} md={6} className="text-center text-lg-start">
            <Fade left>
              <HeroTitle>
                Hi, I'm <span style={{ color: '#3D84C6' }}>{homeData.name}</span>
              </HeroTitle>
              <HeroSubtitle>
                I'm <TypewriterText>{homeData.roles[currentRole]}</TypewriterText>
              </HeroSubtitle>
              <HeroDescription>
                Passionate full-stack developer and AI/ML engineer creating innovative 
                solutions that bridge technology and real-world impact. Let's build 
                something amazing together!
              </HeroDescription>
              <CTAContainer>
                <CTAButton 
                  primary 
                  onClick={() => scrollToSection('about')}
                >
                  About Me
                </CTAButton>
                <CTAButton 
                  onClick={() => scrollToSection('projects')}
                >
                  View Projects
                </CTAButton>
              </CTAContainer>
              <SocialContainer className="justify-content-center justify-content-lg-start">
                {socialData.social.map((social, index) => {
                  const getIcon = (network) => {
                    switch (network) {
                      case 'linkedin': return '💼';
                      case 'github': return '🐙';
                      case 'email': return '📧';
                      case 'twitter': return '🐦';
                      default: return '🔗';
                    }
                  };

                  return (
                    <SocialLink
                      key={index}
                      href={social.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      title={social.network}
                    >
                      {getIcon(social.network)}
                    </SocialLink>
                  );
                })}
              </SocialContainer>
            </Fade>
          </Col>
          <Col lg={6} md={6}>
            <Fade right>
              <HeroImageContainer>
                <FloatingElement />
                <FloatingElement />
                <FloatingElement />
                <HeroImage
                  src="/images/about/me2.jpg"
                  alt="Aryan Bartwal - Personal Photo"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/400x500/3D84C6/ffffff?text=Hero+Image';
                  }}
                />
              </HeroImageContainer>
            </Fade>
          </Col>
        </Row>
      </Container>
    </HomeSection>
  );
};

export default Home;
