import React from 'react';
import { Container, Row, Col } from 'react-bootstrap';
import Fade from 'react-reveal/Fade';
import styled from 'styled-components';

const GallerySection = styled.section`
  min-height: 60vh;
  display: flex;
  align-items: center;
  background: ${({ theme }) => theme.background};
  padding: 80px 0;
`;

const SectionTitle = styled.h2`
  font-size: 2.5rem;
  font-weight: 700;
  text-align: center;
  margin-bottom: 50px;
  color: ${({ theme }) => theme.color};

  &:after {
    content: '';
    display: block;
    width: 50px;
    height: 3px;
    background: ${({ theme }) => theme.accentColor};
    margin: 20px auto;
  }
`;

const GalleryImageContainer = styled.div`
  position: relative;
  overflow: hidden;
  border-radius: 20px;
  box-shadow: 0 15px 35px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease, box-shadow 0.3s ease;

  &:hover {
    transform: translateY(-10px);
    box-shadow: 0 25px 50px rgba(0, 0, 0, 0.15);
  }
`;

const GalleryImage = styled.img`
  width: 100%;
  height: 300px;
  object-fit: cover;
  transition: transform 0.3s ease;

  ${GalleryImageContainer}:hover & {
    transform: scale(1.05);
  }

  @media (max-width: 768px) {
    height: 250px;
  }
`;

const ImageCaption = styled.div`
  position: absolute;
  bottom: 0;
  left: 0;
  right: 0;
  background: linear-gradient(transparent, rgba(0, 0, 0, 0.7));
  color: white;
  padding: 30px 20px 20px;
  font-weight: 500;
  text-align: center;
`;

const Gallery = () => {
  const galleryImages = [
    {
      src: "/personal-e-portfolio/images/about/me-optimized.jpg",
      caption: "Professional Portrait",
      alt: "Aryan Bartwal - Professional Portrait"
    },
    {
      src: "/personal-e-portfolio/images/about/me2.jpg",
      caption: "Casual Moment",
      alt: "Aryan Bartwal - Casual Photo"
    }
  ];

  return (
    <GallerySection id="gallery">
      <Container>
        <SectionTitle>Gallery</SectionTitle>
        <Row className="justify-content-center">
          {galleryImages.map((image, index) => (
            <Col lg={5} md={6} key={index} className="mb-4">
              <Fade bottom delay={index * 200}>
                <GalleryImageContainer>
                  <GalleryImage
                    src={image.src}
                    alt={image.alt}
                    onError={(e) => {
                      e.target.src = 'https://via.placeholder.com/400x300/3D84C6/ffffff?text=Gallery';
                    }}
                  />
                  <ImageCaption>{image.caption}</ImageCaption>
                </GalleryImageContainer>
              </Fade>
            </Col>
          ))}
        </Row>
      </Container>
    </GallerySection>
  );
};

export default Gallery;
