import React, { useState, useEffect } from 'react';
import { Container, Row, Col, Button } from 'react-bootstrap';
import Fade from 'react-reveal/Fade';
import styled, { keyframes } from 'styled-components';

// Animations
const gradientAnimation = keyframes`
  0% { background-position: 0% 50%; }
  50% { background-position: 100% 50%; }
  100% { background-position: 0% 50%; }
`;

const float = keyframes`
  0%, 100% { transform: translateY(0px) rotate(0deg); }
  50% { transform: translateY(-20px) rotate(180deg); }
`;

const pulse = keyframes`
  0%, 100% { transform: scale(1); }
  50% { transform: scale(1.05); }
`;

const slideUp = keyframes`
  from { 
    opacity: 0; 
    transform: translateY(30px); 
  }
  to { 
    opacity: 1; 
    transform: translateY(0); 
  }
`;

const glowPulse = keyframes`
  0%, 100% { 
    box-shadow: 0 0 20px ${props => props.theme.accentColor}30;
  }
  50% { 
    box-shadow: 0 0 40px ${props => props.theme.accentColor}60, 
                0 0 60px ${props => props.theme.accentColor}30;
  }
`;

const HomeSection = styled.section`
  min-height: 100vh;
  display: flex;
  align-items: center;
  background: linear-gradient(-45deg, ${({ theme }) => theme.background}, ${({ theme }) => theme.background}dd, ${({ theme }) => theme.accentColor}20);
  background-size: 400% 400%;
  animation: ${gradientAnimation} 15s ease infinite;
  padding-top: 100px;
  position: relative;
  overflow: hidden;

  &::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: radial-gradient(circle at 20% 80%, ${({ theme }) => theme.accentColor}10 0%, transparent 50%),
                radial-gradient(circle at 80% 20%, ${({ theme }) => theme.accentColor}15 0%, transparent 50%);
    pointer-events: none;
  }
`;

const HeroTitle = styled.h1`
  font-size: 3.5rem;
  font-weight: 700;
  margin-bottom: 20px;
  background: linear-gradient(135deg, ${({ theme }) => theme.color}, ${({ theme }) => theme.accentColor});
  -webkit-background-clip: text;
  -webkit-text-fill-color: transparent;
  background-clip: text;
  line-height: 1.2;

  @media (max-width: 768px) {
    font-size: 2.5rem;
  }
`;

const HeroSubtitle = styled.h2`
  font-size: 1.8rem;
  font-weight: 400;
  margin-bottom: 30px;
  color: ${({ theme }) => theme.accentColor};
  position: relative;

  &::after {
    content: '';
    position: absolute;
    bottom: -5px;
    left: 0;
    width: 0;
    height: 2px;
    background: ${({ theme }) => theme.accentColor};
    animation: ${gradientAnimation} 3s ease-in-out infinite;
  }

  @media (max-width: 768px) {
    font-size: 1.4rem;
  }
`;

const TypewriterText = styled.span`
  position: relative;

  &::after {
    content: '|';
