const Home = () => {
  const [homeData, setHomeData] = useState(null);
  const [socialData, setSocialData] = useState(null);
  const [currentRole, setCurrentRole] = useState(0);
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });

  useEffect(() => {
    const handleMouseMove = (e) => {
      setMousePosition({ x: e.clientX, y: e.clientY });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    fetch('/profile/home.json')
      .then(response => response.json())
      .then(data => setHomeData(data))
      .catch(error => console.error('Error loading home data:', error));

    fetch('/profile/social.json')
      .then(response => response.json())
      .then(data => setSocialData(data))
      .catch(error => console.error('Error loading social data:', error));
  }, []);

  useEffect(() => {
    if (homeData && homeData.roles) {
      const interval = setInterval(() => {
        setCurrentRole((prev) => (prev + 1) % homeData.roles.length);
      }, 3000);
      return () => clearInterval(interval);
    }
  }, [homeData]);

  const scrollToSection = (sectionId) => {
    const element = document.getElementById(sectionId);
    if (element) {
      element.scrollIntoView({ behavior: 'smooth' });
    }
  };

  if (!homeData || !socialData) return null;

  return (
    <HomeSection id="home">
      <Container>
        <Row className="align-items-center">
          <Col lg={6} md={6} className="text-center text-lg-start">
            <Fade left>
              <HeroTitle>
                Hi, I'm <span style={{ color: '#3D84C6' }}>{homeData.name}</span>
              </HeroTitle>
              <HeroSubtitle>
                I'm <TypewriterText>{homeData.roles[currentRole]}</TypewriterText>
              </HeroSubtitle>
              <HeroDescription>
                Passionate full-stack developer and AI/ML engineer creating innovative 
                solutions that bridge technology and real-world impact. Let's build 
                something amazing together!
              </HeroDescription>
              <CTAContainer>
                <CTAButton 
                  primary 
                  onClick={() => scrollToSection('about')}
                >
                  About Me
                </CTAButton>
                <CTAButton 
                  onClick={() => scrollToSection('projects')}
                >
                  View Projects
                </CTAButton>
              </CTAContainer>
              <SocialContainer className="justify-content-center justify-content-lg-start">
                {socialData.social.map((social, index) => {
                  const getIcon = (network) => {
                    switch (network) {
                      case 'linkedin': return '💼';
                      case 'github': return '🐙';
                      case 'email': return '📧';
                      case 'twitter': return '🐦';
                      default: return '🔗';
                    }
                  };

                  return (
                    <SocialLink
                      key={index}
                      href={social.href}
                      target="_blank"
                      rel="noopener noreferrer"
                      title={social.network}
                    >
                      {getIcon(social.network)}
                    </SocialLink>
                  );
                })}
              </SocialContainer>
            </Fade>
          </Col>
          <Col lg={6} md={6}>
            <Fade right>
              <HeroImageContainer>
                <FloatingElement />
                <FloatingElement />
                <FloatingElement />
                <FloatingElement />
                <ParticleElement />
                <ParticleElement />
                <ParticleElement />
                <ParticleElement />
                <HeroImage
                  src="/images/about/me2.jpg"
                  alt="Aryan Bartwal - Personal Photo"
                  onError={(e) => {
                    e.target.src = 'https://via.placeholder.com/350x450/3D84C6/ffffff?text=Aryan+Bartwal';
                  }}
                />
                <ImageGlow />
                <SkillHighlight>
                  <span>🚀 Full-Stack Developer</span>
                </SkillHighlight>
              </HeroImageContainer>
            </Fade>
          </Col>
        </Row>
      </Container>
    </HomeSection>
  );
};

export default Home;
